import {  useState } from "react";
import {useNavigate } from "react-router-dom";
function Usuario(){



    const Ir = useNavigate();
    const [contra, setContra]=useState("*********");
    

    const getSesionData = () =>{
        return localStorage.getItem('token');
    }

    const getUserData = () =>{
        return localStorage.getItem('usuarioregistrado');
    }
    const getPassData = () =>{
        return localStorage.getItem('Contrase単aDelUsuario');
    }

    const usuario=getUserData();
    
    const evento = ()=> {setContra(getPassData())};

    const eventoCierre = () => {
        localStorage.setItem('token', false);
        Ir('/IniciarSesion');}
    
    if(getSesionData===true)
    return(
        <> 
        <h2>Usuario: {usuario}</h2>
        <h3>Contrase単a: {contra} </h3>
        <input type="button" value="Revelar Contrase単a" onClick={evento} />
        <input type="button" value="Cerrar sesion" onClick={eventoCierre} />
        
        </>
    )
}

export default Usuario;
