import { setState } from "react";
function Usuario(){

    const getUserData = () =>{
        return localStorage.getItem('usuarioregistrado');
    }
    const getPassData = () =>{
        return localStorage.getItem('ContraseñaDelUsuario');
    }

    const usuario = getUserData();
    

    const [contra, setContraseña]=setState("'*********'");
    const evento = ()=> {setContraseña(getPassData())
    contraseña=contra;};
    return(
   <> 
        <h2>Usuario: {usuario}</h2>
        <h3>Contraseña: {contra} </h3>
        <input type="button" value="Revelar Contraseña" onClick={evento} />
   </>
    )
}
export default Usuario;
