    console.log(prop.numId);
    var num = prop.numId;
    

    useEffect(()=>{
        axios.get(`http://localhost:3001/Instrumentos/${num}`).then((res)  => {
            setDatos(res.data.respuesta[0]);
            console.log();
        });
    }, []);
    return(
        <div className='tarjeta'>
        <img src={prop.img} alt="" className='imgProd' />
        <h2>Instrumento: {datos.intrumento} - {datos.modelo}</h2>
