import {useState} from "react";
import {useNavigate } from "react-router-dom";



function Registrarse(){

    const Ir = useNavigate();
    const [Usuario, setUsuario]=useState("");
    const [Contraseña, setContraseña]=useState("");
    const [Sesion, setSesion]=useState("false");

    

    const submitcoso = async (e) => {
         e.preventDefault();
         if(Usuario.length===0||Contraseña.length===0){alert("error, rellene ambos campos")} else {
         localStorage.setItem('usuarioregistrado', Usuario);
         localStorage.setItem('ContraseñaDelUsuario', Contraseña);
         localStorage.setItem('EstadoDeSesion', true);
         Ir('/Inicio');}
        }


    return(<><form>
    <h2>Registrarse</h2>
        <input 
            type="text" 
            onChange={(e)=>setUsuario(e.target.value)}
            autoComplete="off"
            placeholder="Usuario"
        />
      <p></p>
        <input 
            type="password" 
            onChange={(e)=>setContraseña(e.target.value)}
            autoComplete="off"
            placeholder="Contraseña"
        />
        <p></p>
        <input type="button" value="Confirmar registro" onClick={submitcoso} />
    </form>
</>
    )
}
export default Registrarse;
